import React, { useState, useEffect } from 'react';
import logo from './nillionimage.svg'; // Ensure this path is correct relative to your file structure
import "../App.css";

const Main = () => {
    const [query, setQuery] = useState("");
    const [history, setHistory] = useState([]);

    // Load history from localStorage when component mounts
    useEffect(() => {
        const storedHistory = JSON.parse(localStorage.getItem("searchHistory")) || [];
        setHistory(storedHistory);
    }, []);

    // Function to handle form submission and store query in history
    const handleSubmit = (e) => {
        e.preventDefault();
        
        if (query.trim() !== "") {
            const updatedHistory = [query, ...history];
            
            // Update local state and localStorage
            setHistory(updatedHistory);
            localStorage.setItem("searchHistory", JSON.stringify(updatedHistory));
            
            // Clear the input
            setQuery("");
        }
    };

    // Function to clear search history
    const clearHistory = () => {
        setHistory([]);
        localStorage.removeItem("searchHistory");
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-900 text-gray-100">
            <main className="flex flex-col items-center py-10 px-6 max-w-lg w-full bg-gray-800 rounded-lg shadow-lg">
                <div className="flex items-center space-x-4 mb-6">
                    <img 
                        src={logo} 
                        alt="Nillion Logo" 
                        className="w-12 h-12 animate-spin-slow" // CSS animation for infinite rotation
                    />
                    <h1 className="text-4xl font-extrabold text-green-500 tracking-wide text-center main-text">
                        MailGuard AI
                    </h1>
                </div>
                <p className="text-lg text-gray-400 mb-8 text-center main-text">Powered by Nillion</p>
                
                <form onSubmit={handleSubmit} className="flex items-center w-full max-w-md mb-6 space-x-2 main-form">
                    <input 
                        type="text" 
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        className="flex-grow p-3 bg-gray-700 text-gray-100 border border-gray-600 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 input-text" 
                        placeholder="Ask me anything..."
                    />
                    <button 
                        type="submit" 
                        className="bg-green-500 text-gray-900 px-5 py-3 rounded-md shadow-md hover:bg-green-600 transition-all duration-300 ease-in-out transform hover:scale-105"
                    >
                        Search
                    </button>
                </form>
                
                {/* Search History Section */}
                <div className="w-full max-w-md bg-gray-700 p-4 rounded-lg shadow-inner mt-4">
                    <h3 className="text-xl font-semibold text-green-400 mb-3">Search History</h3>
                    {history.length > 0 ? (
                        <ul className="space-y-2 text-gray-300">
                            {history.map((item, index) => (
                                <li key={index} className="bg-gray-800 p-2 rounded hover:bg-gray-700 transition">
                                    {item}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p className="text-gray-500">No recent searches.</p>
                    )}
                    {history.length > 0 && (
                        <button 
                            onClick={clearHistory}
                            className="mt-4 text-red-500 hover:text-red-400 hover:underline transition-all"
                        >
                            Clear History
                        </button>
                    )}
                </div>
                
                <div className="flex flex-col items-center mt-6">
                    <p className="text-gray-400">Need help?</p>
                    <div className="flex space-x-6 mt-3">
                        <a href="#" className="text-green-500 hover:text-green-400 hover:underline transition-all duration-200">Help Center</a>
                        <a href="#" className="text-green-500 hover:text-green-400 hover:underline transition-all duration-200">Contact Support</a>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default Main;
