// Sidebar.js
import React from 'react';

const Sidebar = ({ history }) => {
    return (
        <div className="w-64 h-full bg-white shadow-lg px-4 py-6 space-y-4 border-r border-gray-200">
            <h2 className="text-xl font-bold text-indigo-700 mb-4">History</h2>
            <ul className="space-y-2">
                {history.length ? (
                    history.map((item, index) => (
                        <li key={index} className="text-gray-600 hover:text-indigo-600 cursor-pointer">
                            {item}
                        </li>
                    ))
                ) : (
                    <p className="text-gray-500">No history available</p>
                )}
            </ul>
        </div>
    );
};

export default Sidebar;
