import React, { useState } from 'react';

const Header = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    return (
        <header className="flex items-center justify-between p-4 bg-white shadow-md">
            <div>
                <span className="text-xl font-bold">Logo</span>
            </div>
            <nav className="hidden md:flex space-x-4">
                <a href="#" className="text-blue-500 px-4">Home</a>
                <a href="#" className="text-blue-500 px-4">About</a>
                <a href="#" className="text-blue-500 px-4">Services</a>
                <a href="#" className="text-blue-500 px-4">Contact</a>
            </nav>
            <div className="md:hidden">
                <button onClick={toggleMenu} className="text-blue-500 focus:outline-none">
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7"></path>
                    </svg>
                </button>
            </div>
            {/* Mobile Menu */}
            {isOpen && (
                <nav className="absolute top-16 left-0 w-full bg-white shadow-md md:hidden flex flex-col items-center space-y-4 p-4">
                    <a href="#" className="text-blue-500 px-4">Home</a>
                    <a href="#" className="text-blue-500 px-4">About</a>
                    <a href="#" className="text-blue-500 px-4">Services</a>
                    <a href="#" className="text-blue-500 px-4">Contact</a>
                </nav>
            )}
        </header>
    );
};

export default Header;
